public class Initializer {

    int check = 1;

    static { // immmer ausgef√ºhrt mit der Klasse
        System.out.println("Initialize: Static");
    }

    {  // instanzInitializer immer wenn instanziert
        System.out.println("Instance Initializer " + check);
        testMethode();
    }

    public Initializer() {
        System.out.println("Constructor Initializer " + check);
    }
    public void testMethode() {
        try {
            System.out.println(getClass().getMethod("testMethode").getName());
        } catch (NoSuchMethodException e) {
            e.printStackTrace();
        }
    }
}
