    public enum Alkohol {
        WEIN(12), BIER(5.2), BANNANE(1), WASSER(0);
        private final double alkoProzent;

        Alkohol(double alkoProzent) {
            this.alkoProzent = alkoProzent;
        }

        public double alkoGehalt() {
            return alkoProzent;
        }

        public String abbauZeit(double menge, double gewicht) {
            double mengeAlko = menge * alkoGehalt() / 100;
            double abbauZeiten = 9090.9 * mengeAlko / gewicht;
            return "Abbaudauer " + (new DecimalFormat("0.0").format(abbauZeiten) + "h bei " + menge + " liter " + toString() + " bei  Gewicht von: " + gewicht);

        }
    }

    public void enumTest0(Alkohol alko, double menge, double gewicht) {
        System.out.println("Alkoholgehalt von " + alko + " ist: " + alko.alkoGehalt());
        System.out.println(alko.abbauZeit(menge, gewicht));

    }

